CREATE TABLE medicine_doctor (id BIGINT AUTO_INCREMENT, doctor_id BIGINT NOT NULL, type VARCHAR(255), name VARCHAR(255) NOT NULL, spcialisation VARCHAR(255) NOT NULL, photo VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE medicine_drugs (id BIGINT AUTO_INCREMENT, drug_id BIGINT NOT NULL, type VARCHAR(255), company VARCHAR(255) NOT NULL, logo VARCHAR(255), url VARCHAR(255), description TEXT NOT NULL, price TEXT NOT NULL, instock TINYINT DEFAULT '0' NOT NULL, expires_at DATETIME NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE medicine_user (id BIGINT AUTO_INCREMENT, user_id BIGINT NOT NULL, type VARCHAR(255), name VARCHAR(255) NOT NULL, password VARCHAR(255) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE medicine_user_info (id INT AUTO_INCREMENT, fullname TEXT, country TEXT, phone TEXT, website TEXT, PRIMARY KEY(id)) ENGINE = INNODB;
